import React, { useState } from 'react';
import axios from 'axios';

function AddProfessor() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    specialty: '',
    course_id: ''
  });

  const handleChange = (e) => {
    setFormData({ 
      ...formData, 
      [e.target.name]: e.target.value 
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/professors', formData);
      alert('Professor added successfully!');
      setFormData({ name: '', email: '', specialty: '', course_id: '' });
    } catch (error) {
      console.error(error);
      alert('Failed to add professor');
    }
  };

  return (
    <div className="form-container">
      <h2>Add Professor</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
        <input type="text" name="specialty" placeholder="Specialty" value={formData.specialty} onChange={handleChange} required />
        <input type="text" name="course_id" placeholder="Course ID (optional)" value={formData.course_id} onChange={handleChange} />
        <button type="submit">Add Professor</button>
      </form>
    </div>
  );
}

export default AddProfessor;
